mapper.select:
    		SELECT mid, name, address, famname FROM NEIGHBORS N INNER JOIN MEMBERS M ON N.ASKMID = M.MID WHERE N.ACCEPTMID = #{uid} AND state = 1
			UNION
			SELECT mid,name, address, famname FROM NEIGHBORS N INNER JOIN MEMBERS M ON N.ACCEPTMID = M.MID WHERE N.ASKMID = #{uid} AND state = 1;
